def goodSegment(badNumbers,l,r):
    maxLength=0
    adNumbers = sorted(set(badNumbers))
    badNumbers.insert(0,l)
    badNumbers.insert(len(badNumbers)+1,r)
    badNumbers = sorted(badNumbers)
    #print(badNumbers)
    lindex = badNumbers.index(l)
    rindex = badNumbers.index(r)
    #print(lindex,rindex)
    for i in range(lindex,rindex):
        #print(badNumbers[i])
        start = badNumbers[i] if i == 0 else badNumbers[i] + 1
        end = badNumbers[i+1] if i == rindex-1 else badNumbers[i+1] - 1
        #print("[",start,end,"]")
        if maxLength < end - start + 1:
            maxLength = end - start + 1
    print("[",start,end,"]")
    print("Maximum length : ",maxLength)
    return maxLength

